// ðŸ”¹ Basic keyword â†’ intent mapping
// Swap this stub for your MCP / LLM call later
const raw = items[0].json;
const text = (raw.text || '').toLowerCase();
const intent = text.includes('booking')     ? 'booking'
            : text.includes('maintenance') ? 'maintenance'
            : text.includes('report')      ? 'incident_report'
            : 'general_query';
return [{
  json: {
    received_at: new Date().toISOString(),
    intent,
    original_payload: raw,
    ...raw
  }
}];

